$(document).ready(function(){$(".sub-menu a[data-category]").mouseover(function(){var a=$(this).attr("data-category");$(".menu-products-anchor[data-category="+a+"]").attr("id","hover-item")}).mouseout(function(){var a=$(this).attr("data-category");$(".menu-products-anchor[data-category="+a+"]").removeAttr("id","hover-item")})});